body.script:
// Economic Data Visualization - Subtle curves and lines
const canvas = document.getElementById('economicCanvas');
const ctx = canvas.getContext('2d');

canvas.width = window.innerWidth;
canvas.height = window.innerHeight;

class EconomicCurve {
    constructor() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.length = Math.random() * 200 + 100;
        this.angle = Math.random() * Math.PI * 2;
        this.speed = Math.random() * 0.0005 + 0.0002;
        this.opacity = Math.random() * 0.3 + 0.1;
        this.phase = Math.random() * Math.PI * 2;
    }

    update() {
        this.phase += this.speed;
    }

    draw() {
        ctx.strokeStyle = `rgba(74, 124, 89, ${this.opacity})`;
        ctx.lineWidth = 1;
        ctx.beginPath();

        for (let i = 0; i < this.length; i++) {
            const x = this.x + Math.cos(this.angle) * i + Math.sin(i * 0.05 + this.phase) * 30;
            const y = this.y + Math.sin(this.angle) * i + Math.cos(i * 0.05 + this.phase) * 30;

            if (i === 0) {
                ctx.moveTo(x, y);
            } else {
                ctx.lineTo(x, y);
            }
        }

        ctx.stroke();
    }
}

class DataPoint {
    constructor() {
        this.reset();
    }

    reset() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.vx = (Math.random() - 0.5) * 0.3;
        this.vy = (Math.random() - 0.5) * 0.3;
        this.size = Math.random() * 2 + 1;
        this.opacity = Math.random() * 0.4 + 0.1;
    }

    update() {
        this.x += this.vx;
        this.y += this.vy;

        if (this.x < 0 || this.x > canvas.width) this.vx *= -1;
        if (this.y < 0 || this.y > canvas.height) this.vy *= -1;
    }

    draw() {
        ctx.fillStyle = `rgba(107, 158, 120, ${this.opacity})`;
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fill();
    }
}

const curves = Array.from({ length: 8 }, () => new EconomicCurve());
const points = Array.from({ length: 40 }, () => new DataPoint());

function animate() {
    ctx.fillStyle = 'rgba(15, 20, 25, 0.05)';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    curves.forEach(curve => {
        curve.update();
        curve.draw();
    });

    points.forEach(point => {
        point.update();
        point.draw();
    });

    requestAnimationFrame(animate);
}

animate();

window.addEventListener('resize', () => {
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
});

// Subtle parallax effect
let mouseX = 0;
let mouseY = 0;

document.addEventListener('mousemove', (e) => {
    mouseX = (e.clientX - window.innerWidth / 2) * 0.005;
    mouseY = (e.clientY - window.innerHeight / 2) * 0.005;

    document.querySelectorAll('.section').forEach((section, index) => {
        const depth = (index + 1) * 0.5;
        section.style.transform = `translate(${mouseX * depth}px, ${mouseY * depth}px)`;
    });
});
